template.value:
$.ajax({
                type: "GET",
                data: {
                    "driverID": driverID,
                    "current_order_timing": current_order_timing,
                    "delivery_date": delivery_date
                },
                url: "ajax_get_driver_active_colors.php",
                success: function (data) {
                    arr = $.parseJSON(data);

                    console.log(arr);

                    $.each(arr, function (key, value) {
                        console.log(value);

                        if (value) {
                            var selector = "#boxcolor option[value=" + value + "]";
                            // console.log(selector);
                            // console.log($(selector).val());
                            $(selector).append('-[USED]');
                            $(selector).prop("disabled", true);
                        }
                    });
                }
            });
